import React, { useState } from 'react';
import classNames from '../utils/class-names';
import { minutesToDuration, secondsToDuration } from '../utils/duration';
import useInterval from '../utils/useInterval';

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  //Focus time starts at 25 minutes/1500 seconds
  const [focusTimer, setFocusTimer] = useState(1500);
  //Break time starts at 5 minutes/300 seconds
  const [breakTimer, setBreakTimer] = useState(300);
  //user inputted times for focus and break
  const [inputFocusTime, setInputFocusTime] = useState(25);
  const [inputBreakTime, setInputBreakTime] = useState(5);

  const [isSessionStopped, setIsSessionStopped] = useState(true);

  //increase FocusTime no more than 60 minutes
  const handleIncreaseFocusClick = () => {
    setInputFocusTime(Math.min(inputFocusTime + 5, 60));
    setFocusTimer((focusTimer) => (focusTimer = inputFocusTime * 60));
  };
  //decrease FocusTime no less than 5 minutes
  const handleDecreaseFocusClick = () => {
    setInputFocusTime(Math.max(inputFocusTime - 1, 1));
    setFocusTimer((focusTimer) => (focusTimer = inputFocusTime * 60));
  };
  //increase BreakTime no more than 15 minutes
  const handleIncreaseBreakClick = () => {
    setInputBreakTime(Math.min(inputBreakTime + 1, 15));
    setBreakTimer((breakTimer) => (breakTimer = inputBreakTime * 60));
  };
  //decrease BreakTime no less than 1 minute
  const handleDecreaseBreakClick = () => {
    setInputBreakTime(Math.max(inputBreakTime - 1, 1));
    setBreakTimer((breakTimer) => (breakTimer = inputBreakTime * 60));
  };

  useInterval(
    () => {
      setFocusTimer(Math.max(focusTimer - 1, 0));

      if (focusTimer === 0) {
        new Audio(
          `${process.env.PUBLIC_URL}/alarm/submarine-dive-horn.mp3`
        ).play();
        setBreakTimer(Math.max(breakTimer - 1, 0));
      }

      // ToDo: Implement what should happen when the timer is running
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  function stopSession() {
    setIsTimerRunning(false);
    setIsSessionStopped(true);
    setFocusTimer(1500);
    setBreakTimer(300);
    setInputBreakTime(5);
    setInputFocusTime(25);
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {minutesToDuration(inputFocusTime)}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={handleDecreaseFocusClick}
                disabled={isTimerRunning}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={handleIncreaseFocusClick}
                disabled={isTimerRunning}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {minutesToDuration(inputBreakTime)}
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={handleDecreaseBreakClick}
                  disabled={isTimerRunning}
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={handleIncreaseBreakClick}
                  disabled={isTimerRunning}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  'oi-media-play': !isTimerRunning,
                  'oi-media-pause': isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              onClick={stopSession}
              disabled={!isTimerRunning}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* <span
          className={classNames({
            invisible: isSessionStopped,
            visible: !isSessionStopped,
          })}
        /> */}

        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">
              {focusTimer > 0
                ? `Focusing for ${minutesToDuration(inputFocusTime)} minutes`
                : `On Break for ${minutesToDuration(inputBreakTime)} minutes`}
            </h2>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {focusTimer > 0
                ? secondsToDuration(focusTimer)
                : secondsToDuration(breakTimer)}{' '}
              remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: '20px' }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={
                  focusTimer > 0
                    ? 100 - (focusTimer / (inputFocusTime * 60)) * 100
                    : 100 - (breakTimer / (inputBreakTime * 60)) * 100
                } // TODO: Increase aria-valuenow as elapsed time increases
                style={
                  focusTimer > 0
                    ? {
                        width: `${
                          100 - (focusTimer / (inputFocusTime * 60)) * 100
                        }%`,
                      }
                    : {
                        width: `${
                          100 - (breakTimer / (inputBreakTime * 60)) * 100
                        }%`,
                      }
                } // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
