import React, { useState } from 'react';
import classNames from '../utils/class-names';
import { minutesToDuration, secondsToDuration } from '../utils/duration';
import useInterval from '../utils/useInterval';
import ChangeBreakTimer from './ChangeBreakTimer';
import ChangeFocusTimer, { inputFocusTime } from './ChangeFocusTimer';

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  //Focus time starts at 25 minutes/1500 seconds
  const [focusTimer, setFocusTimer] = useState(1500);
  //Break time starts at 5 minutes/300 seconds
  const [breakTimer, setBreakTimer] = useState(300);
  //user inputted times for focus and break

  const [isSessionStopped, setIsSessionStopped] = useState(true);

  useInterval(
    () => {
      setFocusTimer(Math.max(focusTimer - 1, 0));

      if (focusTimer === 0) {
        const audioEl = document.getElementsByClassName('audio-element')[0];
        audioEl.play();
        setBreakTimer(Math.max(breakTimer - 1, 0));
      }

      // ToDo: Implement what should happen when the timer is running
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  function stopSession() {
    setIsTimerRunning(false);
    setIsSessionStopped(true);
    setFocusTimer(1500);
    setBreakTimer(300);
    //setInputBreakTime(5);
    //setInputFocusTime(25);
  }

  return (
    <div className="pomodoro">
      <audio className="audio-element">
        <source src="https://assets.coderrocketfuel.com/pomodoro-times-up.mp3"></source>
      </audio>
      <div className="row">
        <div className="col">
          <ChangeFocusTimer
            passFocusTime={setFocusTimer}
            isTimerRunning={isTimerRunning}
          />
        </div>
        <div className="col">
          <div className="float-right">
            <ChangeBreakTimer
              passBreakTime={setBreakTimer}
              isTimerRunning={isTimerRunning}
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  'oi-media-play': !isTimerRunning,
                  'oi-media-pause': isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              onClick={stopSession}
              disabled={!isTimerRunning}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* <span
          className={classNames({
            invisible: isSessionStopped,
            visible: !isSessionStopped,
          })}
        /> */}

        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">
              {focusTimer > 0
                ? `Focusing for ${secondsToDuration(inputFocusTime)} minutes`
                : `On Break for ${secondsToDuration(breakTimer)} minutes`}
            </h2>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {focusTimer > 0
                ? secondsToDuration(focusTimer)
                : secondsToDuration(breakTimer)}{' '}
              remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: '20px' }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={
                  focusTimer > 0
                    ? 100 - (focusTimer / (focusTimer * 60)) * 100
                    : 100 - (breakTimer / (breakTimer * 60)) * 100
                } // TODO: Increase aria-valuenow as elapsed time increases
                style={
                  focusTimer > 0
                    ? {
                        width: `${
                          100 - (focusTimer / (focusTimer * 60)) * 100
                        }%`,
                      }
                    : {
                        width: `${
                          100 - (breakTimer / (breakTimer * 60)) * 100
                        }%`,
                      }
                } // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
